async function login() {
	const id = document.querySelector("#id").value;
	const pw = document.querySelector("#password").value;
	const data = {id, pw };

	try {
		const response = await fetch("member/login", { method: 'post', body: JSON.stringify(data),headers: {
            "Content-Type": "application/json;charset=utf-8",
        } });
		if (!response.ok) throw new Error(`${response.status} : 비 정상적인 접근입니다.`);

		const responseData = await response.json();
		if (!responseData.ok) throw new Error(`${response.status} : ${responseData.error}`);
		
		$.cookie("name", responseData.name);
		location.href = "index.html";

	} catch (error) {
		processError(error);
	}
}